import React, { useState } from 'react';
import { StyleSheet, Text, View, Button } from 'react-native';

/*
Intended functionality: Came from Sign-up page. Now at "Vendor Sign-up" page. 

Vendor form information Name of business, Email, Type of Food sold.
Store this information in the cloud, ideally; might be hard to let SQL database run all of the information.
*/
export const VenSignupScreen = ({ navigation }) => {
//Source for inspiration: https://aboutreact.com/react-native-login-and-signup/
const [userName, setUserName] = useState('');
const [userEmail, setUserEmail] = useState('');
const [userPassword, setUserPassword] = useState('');
const [errortext, setErrortext] = useState('');
const [isVendor, setIsVendor] = useState(false);
const [isRegistration, setIsRegistration] = useState(false);

const emailInputRef = createRef();
const passwordInputRef = createRef();


const handleButton = () => {
  setErrortext('');
  //Errors if fields not filled in. In order, username, email, created password.
  if (!userName){
      alert ('Need info on username. Please fill in.');
      return;
  }
  if (!userEmail){
      alert ('Need info on user email. Please fill in.');
      return;
  }
  if (!userPassword){
      alert ('Need info on user password. Please fill in.');
      return;
  }
  //Store the information you do get into this, if you do receive info.
  var dataToSend = {
      name: userName,
      email: userEmail,
      password: userPassword,
    };
    //Keep information into a JSON string, coded by key and value.
    var formBody = [];
    for (var key in dataToSend) {
      var encodedKey = encodeURIComponent(key);
      var encodedValue = encodeURIComponent(dataToSend[key]);
      formBody.push(encodedKey + '=' + encodedValue);
    }
    formBody = formBody.join('&');
    //We now have a pretty valid JSON string, made with the key and value given.
    //What we should do with this JSON string is to utilize it to sign up a user.
  };
  <View style={{flex: 1, backgroundColor: '#307ecc'}}>
    <ScrollView
      keyboardShouldPersistTaps="handled"
      contentContainerStyle={{
        justifyContent: 'center',
        alignContent: 'center',
      }}>
      <KeyboardAvoidingView enabled>
        <View style={styles.SectionStyle}>
          <TextInput
            style={styles.inputStyle}
            onChangeText={(UserName) => setUserName(UserName)}
            underlineColorAndroid="#f000"
            placeholder="Enter Name"
            placeholderTextColor="#8b9cb5"
            autoCapitalize="sentences"
            returnKeyType="next"
            onSubmitEditing={() =>
              emailInputRef.current && emailInputRef.current.focus()
            }
            blurOnSubmit={false}
          />
        </View>
        <View style={styles.SectionStyle}>
          <TextInput
            style={styles.inputStyle}
            onChangeText={(UserEmail) => setUserEmail(UserEmail)}
            underlineColorAndroid="#f000"
            placeholder="Enter Email"
            placeholderTextColor="#8b9cb5"
            keyboardType="email-address"
            ref={emailInputRef}
            returnKeyType="next"
            onSubmitEditing={() =>
              passwordInputRef.current &&
              passwordInputRef.current.focus()
            }
            blurOnSubmit={false}
          />
        </View>
        <View style={styles.SectionStyle}>
          <TextInput
            style={styles.inputStyle}
            onChangeText={(UserPassword) =>
              setUserPassword(UserPassword)
            }
            underlineColorAndroid="#f000"
            placeholder="Enter Password"
            placeholderTextColor="#8b9cb5"
            ref={passwordInputRef}
            returnKeyType="next"
            secureTextEntry={true}
            onSubmitEditing={Keyboard.dismiss}
            blurOnSubmit={false}
          />
        </View>
          {errortext != '' ? (
          <Text style={styles.errorTextStyle}>
            {errortext}
          </Text>
          ) : null}
        <TouchableOpacity
          style={styles.buttonStyle}
          activeOpacity={0.5}
          onPress={handleButton}>
          <Text style={styles.buttonTextStyle}>REGISTER</Text>
        </TouchableOpacity>
          </KeyboardAvoidingView>
        </ScrollView>
      </View>
};

const styles = StyleSheet.create({
  SectionStyle: {
    flexDirection: 'row',
    height: 40,
    marginTop: 20,
    marginLeft: 35,
    marginRight: 35,
    margin: 10,
  },
  buttonStyle: {
    backgroundColor: '#7DE24E',
    borderWidth: 0,
    color: '#FFFFFF',
    borderColor: '#7DE24E',
    height: 40,
    alignItems: 'center',
    borderRadius: 30,
    marginLeft: 35,
    marginRight: 35,
    marginTop: 20,
    marginBottom: 20,
  },
  buttonTextStyle: {
    color: '#FFFFFF',
    paddingVertical: 10,
    fontSize: 16,
  },
  inputStyle: {
    flex: 1,
    color: 'white',
    paddingLeft: 15,
    paddingRight: 15,
    borderWidth: 1,
    borderRadius: 30,
    borderColor: '#dadae8',
  },
  errorTextStyle: {
    color: 'red',
    textAlign: 'center',
    fontSize: 14,
  },
  successTextStyle: {
    color: 'white',
    textAlign: 'center',
    fontSize: 18,
    padding: 30,
  },
});
